import React from 'react';
import './FramedGallerySlider.css';
import frame from '../assets/frames/frame-square.png';
import mask from '../assets/frames/frame-square-mask.png'; // masque pour découpe dynamique

const artworks = [
  {
    title: 'Artwork 1',
    artist: '77Briglia',
    imageUrl: 'https://mir-s3-cdn-cf.behance.net/project_modules/2800/9aef2a105305975.643a08df037b7.png',
  },
  {
    title: 'Artwork 2',
    artist: '77Briglia',
    imageUrl: 'https://mir-s3-cdn-cf.behance.net/project_modules/max_1200/3a336f119958217.67b2bea8c64bf.jpg',
  },
  {
    title: 'Artwork 3',
    artist: '77Briglia',
    imageUrl: 'https://mir-s3-cdn-cf.behance.net/project_modules/2800/d83245168424297.643a10cb25ee5.jpg',
  },
];

const FramedGallerySlider = () => {
  return (
    <div className="gallery-slider">
      {artworks.map((artwork, index) => (
        <div key={index} className="frame-container">
          <div className="artwork-wrapper">
            <img src={artwork.imageUrl} alt={artwork.title} className="artwork-image" />
            <img src={frame} alt="Cadre doré Olympus" className="frame-image" />
          </div>
          <div className="plaque">
            <h3>{artwork.title}</h3>
            <p>{artwork.artist}</p>
          </div>
        </div>
      ))}
    </div>
  );
};

export default FramedGallerySlider;
