import React, { useEffect, useState } from 'react';
import { motion } from 'framer-motion';
import { useNavigate } from 'react-router-dom';

const introVariants = {
  hidden: { opacity: 0, scale: 0.95, y: 20 },
  visible: {
    opacity: 1,
    scale: 1,
    y: 0,
    transition: { duration: 1.2, ease: 'easeOut' },
  },
  exit: {
    opacity: 0,
    scale: 0.9,
    y: -20,
    transition: { duration: 0.8, ease: 'easeIn' },
  },
};

const textGlowVariants = {
  hidden: { opacity: 0 },
  visible: {
    opacity: 1,
    transition: { delay: 0.6, duration: 1.4, ease: 'easeOut' },
  },
};

const Intro = () => {
  const navigate = useNavigate();
  const [hasInteracted, setHasInteracted] = useState(false);

  useEffect(() => {
    const seenIntro = localStorage.getItem('seenIntro');
    if (seenIntro) navigate('/accueil');
  }, [navigate]);

  const handleEnter = () => {
    localStorage.setItem('seenIntro', true);
    setHasInteracted(true);
    const audio = new Audio('/assets/audio/intro-sound.mp3');
    audio.play();
    setTimeout(() => navigate('/accueil'), 400);
  };

  return (
    <motion.div
      className="intro"
      variants={introVariants}
      initial="hidden"
      animate="visible"
      exit="exit"
      style={{
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
        alignItems: 'center',
        minHeight: '100vh',
        backgroundColor: '#ebebed',
        color: '#4f4f58',
        transition: 'background-color 0.5s ease',
      }}
    >
      <motion.h1
        className="logo"
        variants={textGlowVariants}
        style={{
          fontSize: '4.2rem',
          fontFamily: 'Georgia, serif',
          margin: 0,
          textShadow: '0 0 10px rgba(255, 215, 0, 0.3)',
        }}
      >
        Olympus
      </motion.h1>
      <motion.p
        className="tagline"
        variants={textGlowVariants}
        style={{
          fontSize: '1.4rem',
          marginTop: '1.5rem',
          fontStyle: 'italic',
          textAlign: 'center',
        }}
      >
        L'entrée d’un sanctuaire d’œuvres sacrées
      </motion.p>

      <motion.button
        onClick={handleEnter}
        whileHover={{ scale: 1.05 }}
        whileTap={{ scale: 0.95 }}
        style={{
          marginTop: '2.5rem',
          padding: '0.75rem 1.8rem',
          border: '1px solid #4f4f58',
          background: 'transparent',
          color: '#4f4f58',
          fontWeight: '500',
          fontSize: '1rem',
          cursor: 'pointer',
          borderRadius: '6px',
        }}
      >
        ENTRER DANS L'OLYMPE
      </motion.button>
    </motion.div>
  );
};

export default Intro;
