import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { useNavigate } from 'react-router-dom';

const introVariants = {
  hidden: { opacity: 0, scale: 0.8, y: 20 },
  visible: {
    opacity: 1,
    scale: 1,
    y: 0,
    transition: { duration: 1.5, ease: 'easeOut' },
  },
  exit: {
    opacity: 0,
    scale: 0.8,
    y: 20,
    transition: { duration: 1 },
  },
};

const textGlowVariants = {
  hidden: { textShadow: '0px 0px 0px rgba(255, 215, 0, 0)' },
  visible: {
    textShadow: '0px 0px 20px rgba(255, 215, 0, 1)',
    transition: { duration: 1.5, ease: 'easeOut' },
  },
};

const Intro = () => {
  const [entered, setEntered] = useState(false);
  const navigate = useNavigate();

  const handleEnter = () => {
    const audio = new Audio('/assets/audio/intro-sound.mp3');
    audio.play().catch(() => console.log("Audio blocked by browser"));

    setEntered(true);

    setTimeout(() => {
      navigate('/portfolio');
    }, 1500);
  };

  return (
    <AnimatePresence>
      {!entered && (
        <motion.div
          className="intro"
          variants={introVariants}
          initial="hidden"
          animate="visible"
          exit="exit"
          style={{
            display: 'flex',
            flexDirection: 'column',
            justifyContent: 'center',
            alignItems: 'center',
            minHeight: '100vh',
            backgroundColor: '#000',
            color: '#fff',
            zIndex: 9999,
          }}
        >
          <motion.h1
            className="logo"
            variants={textGlowVariants}
            style={{
              fontSize: '4rem',
              fontFamily: 'serif',
              margin: 0,
            }}
          >
            Olympus
          </motion.h1>
          <motion.p
            className="tagline"
            variants={textGlowVariants}
            style={{
              fontSize: '1.5rem',
              marginTop: '1rem',
              fontStyle: 'italic',
            }}
          >
            L'entrée d'un sanctuaire d'œuvres sacrées
          </motion.p>
          <motion.button
            onClick={handleEnter}
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            style={{
              marginTop: '3rem',
              background: 'transparent',
              color: '#fff',
              border: '1px solid #fff',
              padding: '1rem 2rem',
              fontSize: '1rem',
              cursor: 'pointer',
              textTransform: 'uppercase',
              backdropFilter: 'blur(8px)',
            }}
          >
            Entrer dans l'Olympe
          </motion.button>
        </motion.div>
      )}
    </AnimatePresence>
  );
};

export default Intro;
