// Layout.jsx
import React, { useState, useEffect } from 'react';
import Navbar from './Navbar';
import PageTransition from './PageTransition';
import Loader from './Loader';
import MouseCursor from './MouseCursor';
import AudioControls from './AudioControls';

const Layout = ({ children }) => {
  // Gestion du Dark Mode avec persistance via localStorage
  const [darkMode, setDarkMode] = useState(false);

  useEffect(() => {
    const savedMode = localStorage.getItem('darkMode') === 'true';
    setDarkMode(savedMode);
  }, []);

  const toggleDarkMode = () => {
    setDarkMode(!darkMode);
    localStorage.setItem('darkMode', !darkMode);
  };

  return (
    <div className={darkMode ? 'dark-mode' : ''}>
      {/* Curseur personnalisé et contrôles audio pour une ambiance sacrée */}
      <MouseCursor />
      <AudioControls />
      
      {/* Barre de navigation avec toggle du dark mode */}
      <Navbar toggleDarkMode={toggleDarkMode} darkMode={darkMode} />
      
      {/* Transitions fluides entre pages */}
      <PageTransition>
        {children}
      </PageTransition>
      
      {/* Loader sacré pour masquer les temps de chargement */}
      <Loader />
    </div>
  );
};

export default Layout;
