import React, { useEffect, useRef, useState } from 'react';

const AudioControls = () => {
  const audioRef = useRef(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [volume, setVolume] = useState(0.5);

  useEffect(() => {
    const audio = audioRef.current;
    if (audio) {
      audio.volume = volume;
    }
  }, [volume]);

  const togglePlay = () => {
    const audio = audioRef.current;
    if (!audio) return;

    if (audio.paused) {
      audio.play().then(() => setIsPlaying(true)).catch(() => {});
    } else {
      audio.pause();
      setIsPlaying(false);
    }
  };

  const handleVolumeChange = (e) => {
    const newVolume = parseFloat(e.target.value);
    setVolume(newVolume);
    if (audioRef.current) {
      audioRef.current.volume = newVolume;
    }
  };

  return (
    <div style={{ position: 'fixed', bottom: 20, right: 20, zIndex: 10000, display: 'flex', alignItems: 'center', gap: '1rem' }}>
      <button
        onClick={togglePlay}
        style={{
          background: 'transparent',
          border: '1px solid #4f4f58',
          color: '#4f4f58',
          padding: '0.5rem 1rem',
          borderRadius: '6px',
          fontWeight: 500,
        }}
      >
        {isPlaying ? '⏸ Ambiance' : '▶️ Ambiance'}
      </button>
      <input
        type="range"
        min="0"
        max="1"
        step="0.01"
        value={volume}
        onChange={handleVolumeChange}
        style={{ width: '100px' }}
      />
      <audio ref={audioRef} loop src="/assets/audio/ambience-loop.mp3" />
    </div>
  );
};

export default AudioControls;
