import React, { useState } from 'react';
import './FramedGalleryDisplay.css';

const artworks = [
  {
    title: 'Crimson Halo',
    description: 'Digital piece echoing divine contrast and sacred glow.',
    image: '/assets/artworks/artwork-01.png'
  },
  {
    title: 'Divine Spiral',
    description: 'A baroque swirl rendered in celestial harmony.',
    image: '/assets/artworks/artwork-02.png'
  },
  {
    title: 'Olympian Light',
    description: 'Fractal light piercing a marbled heaven.',
    image: '/assets/artworks/artwork-03.png'
  }
];

const FramedGalleryDisplay = () => {
  const [currentIndex, setCurrentIndex] = useState(0);

  const next = () => {
    setCurrentIndex((prev) => (prev + 1) % artworks.length);
  };

  const prev = () => {
    setCurrentIndex((prev) => (prev - 1 + artworks.length) % artworks.length);
  };

  const { title, description, image } = artworks[currentIndex];

  return (
    <div className="framed-gallery-wrapper">
      <div className="frame-container">
        <img src="/assets/frames/frame-square.png" alt="Golden Frame" className="frame-overlay" />
        <img src={image} alt={title} className="framed-image" />
      </div>

      <div className="art-plaque">
        <h2>{title}</h2>
        <p>{description}</p>
      </div>

      <div className="frame-nav">
        <button onClick={prev}>&larr;</button>
        <span>{currentIndex + 1} / {artworks.length}</span>
        <button onClick={next}>&rarr;</button>
      </div>
    </div>
  );
};

export default FramedGalleryDisplay;
