import React, { useEffect, useRef, useState } from 'react';

const AudioControls = () => {
  const audioRef = useRef(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [volume, setVolume] = useState(0.5);
  const [showSlider, setShowSlider] = useState(false);

  useEffect(() => {
    const audio = audioRef.current;
    if (audio) {
      audio.volume = volume;
    }
  }, [volume]);

  const togglePlay = () => {
    const audio = audioRef.current;
    if (!audio) return;

    if (audio.paused) {
      audio.play().then(() => setIsPlaying(true)).catch(() => {});
    } else {
      audio.pause();
      setIsPlaying(false);
    }
  };

  const handleVolumeChange = (e) => {
    const newVolume = parseFloat(e.target.value);
    setVolume(newVolume);
    if (audioRef.current) {
      audioRef.current.volume = newVolume;
    }
  };

  return (
    <div style={{ position: 'fixed', bottom: 20, right: 20, zIndex: 10000, display: 'flex', alignItems: 'flex-end', flexDirection: 'column', gap: '0.5rem' }}>
      <div
        onClick={() => setShowSlider((prev) => !prev)}
        style={{ cursor: 'pointer', textAlign: 'center' }}
      >
        <button
          onClick={togglePlay}
          style={{
            background: 'transparent',
            border: '1px solid #4f4f58',
            color: '#4f4f58',
            padding: '0.5rem 1rem',
            borderRadius: '6px',
            fontWeight: 500,
          }}
        >
          {isPlaying ? '⏸ Ambiance' : '▶️ Ambiance'}
        </button>
      </div>

      {showSlider && (
        <input
          type="range"
          min="0"
          max="1"
          step="0.01"
          value={volume}
          onChange={handleVolumeChange}
          style={{
            writingMode: 'bt-lr',
            WebkitAppearance: 'slider-vertical',
            height: '100px',
            width: '6px',
            background: '#e0e0e0',
            borderRadius: '5px',
            border: 'none',
            outline: 'none',
            accentColor: 'transparent',
          }}
        />
      )}

      <audio ref={audioRef} loop src="/assets/audio/ambience-loop.mp3" />
    </div>
  );
};

export default AudioControls;
