import React, { useState, useEffect } from 'react';
import './FramedGallerySlider.css';
import frameImage from '/assets/frames/frame-image.png'; // Assurez-vous que le chemin est correct

const artworks = [
  {
    src: '/assets/artworks/artwork1.jpg',
    title: 'Titre de l\'œuvre 1',
    description: 'Description de l\'œuvre 1.',
  },
  {
    src: '/assets/artworks/artwork2.jpg',
    title: 'Titre de l\'œuvre 2',
    description: 'Description de l\'œuvre 2.',
  },
  // Ajoutez d'autres œuvres ici
];

const FramedGallerySlider = () => {
  const [currentIndex, setCurrentIndex] = useState(0);

  const nextSlide = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex === artworks.length - 1 ? 0 : prevIndex + 1
    );
  };

  const prevSlide = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex === 0 ? artworks.length - 1 : prevIndex - 1
    );
  };

  useEffect(() => {
    const interval = setInterval(() => {
      nextSlide();
    }, 5000); // Change d'image toutes les 5 secondes
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="framed-gallery-slider">
      <div className="nav-buttons">
        <button onClick={prevSlide} aria-label="Précédent">⟵</button>
        <button onClick={nextSlide} aria-label="Suivant">⟶</button>
      </div>
      <div className="artwork-wrapper">
        <div className="frame-mask-wrapper">
          <img src={frameImage} alt="Cadre" className="frame-image" />
          <img
            src={artworks[currentIndex].src}
            alt={artworks[currentIndex].title}
            className="masked-art"
          />
        </div>
        <div className="art-description">
          <h2>{artworks[currentIndex].title}</h2>
          <p>{artworks[currentIndex].description}</p>
        </div>
      </div>
      <div className="pagination-dots">
        {artworks.map((_, index) => (
          <span
            key={index}
            className={`dot ${index === currentIndex ? 'active' : ''}`}
            onClick={() => setCurrentIndex(index)}
          ></span>
        ))}
      </div>
    </div>
  );
};

export default FramedGallerySlider;
