// ProjectGallery.jsx
import React, { useState, useEffect, useRef } from 'react';
import { motion } from 'framer-motion';
import './ProjectGallery.css';
import Parallax from './Parallax';
import { playHoverSound } from '../utils/audioManager';

const ProjectGallery = ({ isLightMode }) => {
  const [selectedArtwork, setSelectedArtwork] = useState(null);
  const [artworks, setArtworks] = useState([]);
  const galleryRef = useRef(null);

  // Simuler le chargement des données des œuvres
  useEffect(() => {
    // Données factices pour la démo
    const demoArtworks = [
      {
        id: 1,
        title: "La Nuit Étoilée",
        artist: "Vincent van Gogh",
        year: "1889",
        description: "\"Quand je regarde les étoiles et avec elles mon être tout entier, je suis partie d'une de ces étoiles.\"",
        image: "/api/placeholder/600/600",
        format: "square",
      },
      {
        id: 2,
        title: "La Création d'Adam",
        artist: "Michel-Ange",
        year: "1512",
        description: "Un fragment emblématique de la fresque du plafond de la chapelle Sixtine.",
        image: "/api/placeholder/800/500",
        format: "landscape",
      },
      {
        id: 3,
        title: "La Joconde",
        artist: "Léonard de Vinci",
        year: "1503",
        description: "Un portrait mondialement célèbre dont le sourire reste mystérieux.",
        image: "/api/placeholder/500/800",
        format: "portrait",
      },
      {
        id: 4,
        title: "Les Tournesols",
        artist: "Vincent van Gogh",
        year: "1888",
        description: "Une série de tableaux représentant des tournesols dans un vase.",
        image: "/api/placeholder/600/600",
        format: "square",
      },
      {
        id: 5,
        title: "La Persistance de la Mémoire",
        artist: "Salvador Dalí",
        year: "1931",
        description: "Une œuvre surréaliste connue pour ses montres molles.",
        image: "/api/placeholder/800/500",
        format: "landscape",
      },
      {
        id: 6,
        title: "Guernica",
        artist: "Pablo Picasso",
        year: "1937",
        description: "Une puissante représentation de la guerre civile espagnole.",
        image: "/api/placeholder/800/500",
        format: "landscape",
      },
      {
        id: 7,
        title: "Le Cri",
        artist: "Edvard Munch",
        year: "1893",
        description: "Une expression de l'angoisse existentielle.",
        image: "/api/placeholder/500/800",
        format: "portrait",
      },
      // Plus d'œuvres peuvent être ajoutées ici
    ];

    setArtworks(demoArtworks);
  }, []);

  const handleArtworkClick = (artwork) => {
    setSelectedArtwork(artwork);
    document.body.style.overflow = 'hidden';
  };

  const closeModal = () => {
    setSelectedArtwork(null);
    document.body.style.overflow = 'auto';
  };

  // Effet de parallaxe au défilement de la page
  const handleScroll = () => {
    const scrollY = window.scrollY;
    const items = document.querySelectorAll('.artwork-frame');

    items.forEach((item, index) => {
      const speed = index % 2 === 0 ? 0.05 : 0.03;
      item.style.transform = `translateY(${scrollY * speed}px)`;
    });
  };

  useEffect(() => {
    window.addEventListener('scroll', handleScroll);
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  // Effet de rotation des œuvres inspiré de l'image de référence
  const handleMouseMove = (e) => {
    if (!galleryRef.current) return;

    const { left, top, width, height } = galleryRef.current.getBoundingClientRect();
    const centerX = left + width / 2;
    const centerY = top + height / 2;

    const mouseX = e.clientX;
    const mouseY = e.clientY;

    const angleX = (mouseY - centerY) / 50;
    const angleY = (centerX - mouseX) / 50;

    const items = document.querySelectorAll('.artwork-frame');
    items.forEach((item, index) => {
      const depth = index % 3 === 0 ? 1 : index % 3 === 1 ? 1.5 : 2;
      item.style.transform = `rotateX(${angleX / depth}deg) rotateY(${angleY / depth}deg)`;
    });
  };

  return (
    <div className={`gallery-container ${isLightMode ? 'light' : 'dark'}`}>
      <div className="marble-background"></div>

      <div className="gallery-header">
        <Parallax offset={-50}>
          <h1 className="gallery-title">LA GALERIE OLYMPUS</h1>
          <p className="gallery-subtitle">Une collection d'œuvres divines</p>
        </Parallax>
      </div>

      <div 
        ref={galleryRef}
        className="gallery-grid" 
        onMouseMove={handleMouseMove}
      >
        {artworks.map((artwork) => (
          <motion.div
            key={artwork.id}
            className={`artwork-frame ${artwork.format}`}
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6, delay: artwork.id * 0.1 }}
            onMouseEnter={() => playHoverSound()}
            onClick={() => handleArtworkClick(artwork)}
            whileHover={{ 
              scale: 1.02, 
              filter: 'brightness(1.1)', 
              boxShadow: '0 0 15px rgba(212, 175, 55, 0.6)'
            }}
          >
            <div className="frame-border">
              <div className="frame-inner">
                <img 
                  src={artwork.image} 
                  alt={artwork.title} 
                  className="artwork-image" 
                  loading="lazy"
                />
                <div className="artwork-info-preview">
                  <h3>{artwork.title}</h3>
                  <p>{artwork.artist}, {artwork.year}</p>
                </div>
              </div>
            </div>
          </motion.div>
        ))}
      </div>

      {/* Modal pour l'aperçu détaillé */}
      {selectedArtwork && (
        <div className="artwork-modal-overlay" onClick={closeModal}>
          <div className="artwork-modal" onClick={(e) => e.stopPropagation()}>
            <button className="modal-close-btn" onClick={closeModal}>×</button>
            <div className="modal-frame">
              <img 
                src={selectedArtwork.image} 
                alt={selectedArtwork.title} 
                className="modal-artwork-image"
              />
            </div>
            <div className="modal-artwork-info">
              <h2>{selectedArtwork.title}</h2>
              <h3>{selectedArtwork.artist}, {selectedArtwork.year}</h3>
              <p className="modal-artwork-description">{selectedArtwork.description}</p>
            </div>
          </div>
        </div>
      )}

      {/* Éléments décoratifs inspirés de l'architecture classique */}
      <div className="decorative-column left-column"></div>
      <div className="decorative-column right-column"></div>
    </div>
  );
};

export default ProjectGallery;
