// Intro.jsx
import React, { useEffect } from 'react';
import { motion } from 'framer-motion';

const introVariants = {
  hidden: { opacity: 0, scale: 0.8, y: 20 },
  visible: {
    opacity: 1,
    scale: 1,
    y: 0,
    transition: {
      duration: 1.5,
      ease: 'easeOut',
    },
  },
  exit: { opacity: 0, scale: 0.8, y: 20, transition: { duration: 1 } },
};

const textGlowVariants = {
  hidden: { textShadow: '0px 0px 0px rgba(255, 215, 0, 0)' },
  visible: {
    textShadow: '0px 0px 20px rgba(255, 215, 0, 1)', // glow doré
    transition: {
      duration: 1.5,
      ease: 'easeOut',
    },
  },
};

const Intro = () => {
  useEffect(() => {
    // Lancement du son d'intro dès le montage du composant
    const audio = new Audio('/assets/audio/intro-sound.mp3');
    audio.play();
  }, []);

  return (
    <motion.div
      className="intro"
      variants={introVariants}
      initial="hidden"
      animate="visible"
      exit="exit"
      style={{
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
        alignItems: 'center',
        minHeight: '100vh',
        backgroundColor: '#000', // fond sombre pour un effet sacré
        color: '#fff',
      }}
    >
      <motion.h1
        className="logo"
        variants={textGlowVariants}
        style={{
          fontSize: '4rem',
          fontFamily: 'serif',
          margin: 0,
        }}
      >
        Olympus
      </motion.h1>
      <motion.p
        className="tagline"
        variants={textGlowVariants}
        style={{
          fontSize: '1.5rem',
          marginTop: '1rem',
          fontStyle: 'italic',
        }}
      >
        L'entrée d'un sanctuaire d'œuvres sacrées
      </motion.p>
    </motion.div>
  );
};

export default Intro;
