import React, { useRef, useEffect, useState } from 'react';
import '../styles/projectgallery.css';

const projects = [
  { id: 1, title: 'Œuvre 1', image: '/assets/images/project1.jpg' },
  { id: 2, title: 'Œuvre 2', image: '/assets/images/project2.jpg' },
  { id: 3, title: 'Œuvre 3', image: '/assets/images/project3.jpg' },
  { id: 4, title: 'Œuvre 4', image: '/assets/images/project4.jpg' },
  { id: 5, title: 'Œuvre 5', image: '/assets/images/project5.jpg' },
];

const ProjectGallery = () => {
  const containerRef = useRef(null);
  const [zoomedProject, setZoomedProject] = useState(null);

  useEffect(() => {
    const container = containerRef.current;
    const onWheel = e => {
      e.preventDefault();
      container.scrollLeft += e.deltaY;
    };
    container.addEventListener('wheel', onWheel, { passive: false });
    return () => container.removeEventListener('wheel', onWheel);
  }, []);

  return (
    <div className="gallery-wrapper">
      <h2 className="gallery-title">Œuvres sacrées</h2>
      <div className="gallery-container" ref={containerRef}>
        {projects.map(project => (
          <div
            key={project.id}
            className="gallery-item"
            onClick={() => setZoomedProject(project)}
          >
            <div className="frame">
              <img src={project.image} alt={project.title} />
              <div className="plaque">{project.title}</div>
            </div>
          </div>
        ))}
      </div>

      {zoomedProject && (
        <div className="zoom-modal" onClick={() => setZoomedProject(null)}>
          <img src={zoomedProject.image} alt={zoomedProject.title} />
          <p className="zoom-title">{zoomedProject.title}</p>
        </div>
      )}
    </div>
  );
};

export default ProjectGallery;
