// ProjectGallery.jsx
import React, { useState } from 'react';
import { motion } from 'framer-motion';
import ZoomModal from './ZoomModal';

const projectItems = [
  { id: 1, title: 'Projet Sacré 1', image: 'https://via.placeholder.com/600x400?text=Projet+1' },
  { id: 2, title: 'Projet Sacré 2', image: 'https://via.placeholder.com/600x400?text=Projet+2' },
  { id: 3, title: 'Projet Sacré 3', image: 'https://via.placeholder.com/600x400?text=Projet+3' },
  // Ajoute d'autres projets si nécessaire
];

const ProjectGallery = () => {
  const [selectedProject, setSelectedProject] = useState(null);
  const openModal = (project) => setSelectedProject(project);
  const closeModal = () => setSelectedProject(null);

  return (
    <div style={{
      overflowX: 'scroll',
      display: 'flex',
      scrollSnapType: 'x mandatory',
      padding: '2rem',
      gap: '1rem'
    }}>
      {projectItems.map(project => (
        <motion.div
          key={project.id}
          onClick={() => openModal(project)}
          whileHover={{ scale: 1.05 }}
          style={{
            minWidth: '300px',
            height: '200px',
            backgroundImage: `url(${project.image})`,
            backgroundSize: 'cover',
            backgroundPosition: 'center',
            scrollSnapAlign: 'center',
            borderRadius: '8px',
            cursor: 'pointer',
            position: 'relative'
          }}
        >
          <div style={{
            position: 'absolute',
            bottom: 0,
            width: '100%',
            background: 'rgba(0, 0, 0, 0.6)',
            color: '#fff',
            padding: '0.5rem',
            textAlign: 'center'
          }}>
            {project.title}
          </div>
        </motion.div>
      ))}
      <ZoomModal isOpen={selectedProject !== null} onClose={closeModal}>
        {selectedProject && (
          <div>
            <h2>{selectedProject.title}</h2>
            <img src={selectedProject.image} alt={selectedProject.title} style={{ width: '100%' }} />
          </div>
        )}
      </ZoomModal>
    </div>
  );
};

export default ProjectGallery;
