// ZoomModal.jsx
import React from 'react';
import { motion } from 'framer-motion';

const modalVariants = {
  hidden: { opacity: 0, scale: 0.8 },
  visible: { opacity: 1, scale: 1, transition: { duration: 0.5, ease: 'easeOut' } },
  exit: { opacity: 0, scale: 0.8, transition: { duration: 0.5, ease: 'easeIn' } },
};

const ZoomModal = ({ isOpen, onClose, children }) => {
  if (!isOpen) return null;
  return (
    <motion.div
      className="zoom-modal-backdrop"
      initial="hidden"
      animate="visible"
      exit="exit"
      style={{
        position: 'fixed',
        top: 0,
        left: 0,
        width: '100vw',
        height: '100vh',
        background: 'rgba(0, 0, 0, 0.8)',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        zIndex: 1000,
      }}
      onClick={onClose}
    >
      <motion.div
        className="zoom-modal-content"
        variants={modalVariants}
        onClick={e => e.stopPropagation()}
        style={{
          background: '#222',
          padding: '2rem',
          borderRadius: '8px',
          maxWidth: '90%',
          maxHeight: '90%',
          overflow: 'auto',
        }}
      >
        {children}
        <button onClick={onClose} style={{ marginTop: '1rem' }}>Fermer</button>
      </motion.div>
    </motion.div>
  );
};

export default ZoomModal;
